mapper.select:
select SUM(finishCounts) as finishCounts, case when LENGTH(trim(MAX(maxExplosiveness)))=0 then 0 else MAX(maxExplosiveness) END maxExplosiveness, ROUND(AVG(avgExplosiveness),2) avgExplosiveness, SUM(totalTrainSeconds) totalTrainSeconds, SUM(case when trainType = "3" then 1 else 0 end) as finishCourseCount, ROUND(SUM(calorie),2) totalCalorie from ( select taah.id, "1" as trainType, taah.userId, taah.actTrainSeconds totalTrainSeconds, taah.maxExplosiveness, taah.avgExplosiveness, taah.finishCounts, taah.calorie calorie, taah.trainEndTime trainTime from train_action_array_head taah where taah.userId = #{userId} and taah.deleted ='0'
and date_format(taah.trainEndTime ,'%Y-%m') = #{month}
and date_format(taah.trainEndTime ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(taah.trainEndTime ,'%Y-%m-%d') = #{chooseDay}
UNION select tfh.id, "2" as trainType, tfh.userId, tfh.actTrainSeconds totalTrainSeconds, tfh.maxExplosiveness, tfh.avgExplosiveness, tfh.finishCounts, tfh.calorie calorie, tfh.vedioEndTime trainTime from train_free_head tfh where tfh.userId = #{userId} and tfh.deleted ='0'
and date_format(tfh.vedioEndTime ,'%Y-%m') = #{month}
and date_format(tfh.vedioEndTime ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(tfh.vedioEndTime ,'%Y-%m-%d') = #{chooseDay}
UNION select tchi.id, "3" as trainType, tchi.userId, tchi.actualTrainSeconds totalTrainSeconds, tchi.maxExplosiveness maxExplosiveness, tchi.avgExplosiveness avgExplosiveness, sum(tcai.finishCounts) as finishCounts, tchi.calorie calorie, tchi.vedioEndTime trainTime from train_course_head_info tchi LEFT JOIN train_course_block_info tcbi ON tcbi.trainCourseHeadInfoId = tchi.id AND tcbi.deleted = 0 LEFT JOIN train_course_act_info tcai on tcai.trainCourseBlockInfoId = tcbi.id AND tcai.deleted = 0 where tchi.userId = #{userId} and tchi.deleted ='0'
and date_format(tchi.vedioEndTime ,'%Y-%m') = #{month}
and date_format(tchi.vedioEndTime ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(tchi.vedioEndTime ,'%Y-%m-%d') = #{chooseDay}
GROUP BY tchi.id
UNION select tfth.id, "4" as trainType, tfth.userId, tfth.actTrainSeconds totalTrainSeconds, tfth.maxExplosiveness, tfth.avgExplosiveness, tfth.finishCounts, tfth.calorie calorie, tfth.createDate trainTime from train_free_train_head tfth where tfth.userId = #{userId} and tfth.deleted ='0'
and date_format(tfth.createDate ,'%Y-%m') = #{month}
and date_format(tfth.createDate ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(tfth.createDate ,'%Y-%m-%d') = #{chooseDay}
) a

