SELECT 4 AS trainType, tfth.id AS trainReportId, "自由训练" AS trainName, tfth.createDate AS trainTime, tfth.actTrainSeconds AS actualTrainSeconds, tfth.finishTotalPower AS finishTotalPower, tfth.finishCounts finishCounts, ROUND(tfth.calorie,2) calorie FROM train_free_train_head tfth WHERE tfth.deleted = 0 AND tfth.userId = #{userTrainInfoQueryRequest.userId}
and 4 = #{userTrainInfoQueryRequest.trainType}
and date_format(tfth.createDate,'%Y-%m') = #{userTrainInfoQueryRequest.month}
and date_format(tfth.createDate,'%Y-%m-%d') BETWEEN #{userTrainInfoQueryRequest.beginWeekDate} AND #{userTrainInfoQueryRequest.endWeekDate}
and date_format(tfth.createDate,'%Y-%m-%d') = #{userTrainInfoQueryRequest.chooseDay}
ORDER BY str_to_date(trainTime,'%Y-%m-%d %H:%i:%s') DESC
