mapper.select:
select SUM(case when trainType = "3" then 1 else 0 end) as finishCourseCount , case when LENGTH(trim(MAX(maxExplosiveness)))=0 then 0 else MAX(maxExplosiveness) END maxExplosiveness, ROUND(AVG(avgExplosiveness),2) avgExplosiveness, SUM(totalTrainSeconds) totalTrainSeconds, SUM(calorie) totalCalorie from ( select id, "1" as trainType, userId, actTrainSeconds totalTrainSeconds, maxExplosiveness, avgExplosiveness, ROUND(calorie,2) calorie from train_action_array_head where userId = #{userId} and deleted ='0'
and date_format(createDate ,'%Y-%m') = #{month}
and date_format(createDate ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(createDate ,'%Y-%m-%d') = #{chooseDay}
UNION select id, "2" as trainType, userId, actTrainSeconds totalTrainSeconds, maxExplosiveness, avgExplosiveness, ROUND(calorie,2) calorie from train_free_head where userId = #{userId} and deleted ='0'
and date_format(createDate ,'%Y-%m') = #{month}
and date_format(createDate ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(createDate ,'%Y-%m-%d') = #{chooseDay}
UNION select id, "3" as trainType, userId, actualTrainSeconds totalTrainSeconds, maxExplosiveness, avgExplosiveness, ROUND(calorie,2) calorie from train_course_head_info where userId = #{userId} and deleted ='0'
and date_format(createDate ,'%Y-%m') = #{month}
and date_format(createDate ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(createDate ,'%Y-%m-%d') = #{chooseDay}
UNION select id, "4" as trainType, userId, actTrainSeconds totalTrainSeconds, maxExplosiveness, avgExplosiveness, ROUND(calorie,2) calorie from train_free_training_head where userId = #{userId} and deleted ='0'
and date_format(createDate ,'%Y-%m') = #{month}
and date_format(createDate ,'%Y-%m-%d') BETWEEN #{beginWeekDate} AND #{endWeekDate}
and date_format(createDate ,'%Y-%m-%d') = #{chooseDay}
) a

SELECT trainType, trainReportId, trainName, trainTime, actualTrainSeconds, finishTotalPower, calorie FROM ( SELECT 1 AS trainType, tchi.id AS trainReportId, tchi.course AS trainName, tchi.vedioEndTime AS trainTime, tchi.actualTrainSeconds AS actualTrainSeconds, tchi.finishTotalPower AS finishTotalPower, ROUND(tchi.calorie,2) calorie FROM train_course_head_info tchi WHERE tchi.deleted = 0 AND tchi.userId = #{userTrainInfoQueryRequest.userId} UNION SELECT 2 AS trainType, tfh.id AS trainReportId, tfh.action AS trainName, tfh.vedioEndTime AS trainTime, tfh.actTrainSeconds AS actualTrainSeconds, tfh.finishTotalPower AS finishTotalPower, ROUND(tfh.calorie,2) calorie FROM train_free_head tfh WHERE tfh.deleted = 0 AND tfh.userId = #{userTrainInfoQueryRequest.userId} UNION SELECT 3 AS trainType, taah.id AS trainReportId, taah.actionArrayName AS trainName, taah.trainBeginTime AS trainTime, taah.actTrainSeconds AS actualTrainSeconds, taah.finishTotalPower AS finishTotalPower, ROUND(taah.calorie,2) calorie FROM train_action_array_head taah where taah.deleted = 0 and taah.userId=#{userTrainInfoQueryRequest.userId} UNION SELECT 4 AS trainType, tfth.id AS trainReportId, tfth.action AS trainName, tfth.vedioEndTime AS trainTime, tfth.actTrainSeconds AS actualTrainSeconds, tfth.finishTotalPower AS finishTotalPower, ROUND(tfth.calorie,2) calorie FROM train_free_training_head tfth WHERE tfth.deleted = 0 AND tfth.userId = #{userTrainInfoQueryRequest.userId} ) a WHERE
a.trainType = #{userTrainInfoQueryRequest.trainType}
and date_format(a.trainTime ,'%Y-%m') = #{userTrainInfoQueryRequest.month}
and date_format(a.trainTime ,'%Y-%m-%d') BETWEEN #{userTrainInfoQueryRequest.beginWeekDate} AND #{userTrainInfoQueryRequest.endWeekDate}
and date_format(a.trainTime ,'%Y-%m-%d') = #{userTrainInfoQueryRequest.chooseDay}
ORDER BY str_to_date(trainTime,'%Y-%m-%d %H:%i:%s') DESC
